function Calendar(t,e){this.tbl=document.getElementById(e);this.isFirst=true;this.year=t.getFullYear();this.month=t.getMonth();this.date=t.getDate()}Calendar.prototype.alterYear=function(t){this.year=t;this.build()};Calendar.prototype.alterMonth=function(t){this.month=t-1;this.build()};Calendar.prototype.setPrev=function(){if(this.month<=0){this.month=11;this.year--}else{this.month--}this.build()};Calendar.prototype.setNext=function(){if(this.month>=11){this.month=0;this.year++}else{this.month++}this.build()};Calendar.prototype.build=function(){var t=0;var e=null;var i=null;var n=this.tbl;var r=this.year;var s=this.month;if(this.isFirst){e=n.insertRow();i=e.insertCell();var l="p_"+n.id;var o="n_"+n.id;i.innerHTML="<button id ="+l+">&lt;</button>";i=e.insertCell();i.colSpan=5;i.innerHTML="<div id= head"+n.id+">"+r+"년"+(s+1)+"월</div>";i=e.insertCell();i.innerHTML="<button id ="+o+">&gt;</button>";var a=["일","월","화","수","목","금","토"];e=n.insertRow();for(t=0;t<a.length;t++){i=e.insertCell();i.textContent=a[t]}}else{while(n.rows.length>2){n.deleteRow(n.rows.length-1)}this.isFirst=false}if(!this.isFirst){document.getElementById("head"+n.id).innerHTML="<div id= head"+n.id+">"+r+"년"+(s+1)+"월</div>"}var h=new Date(r,s,1);var d=new Date(r,s+1,0);e=n.insertRow();var u=0;for(t=0;t<h.getDay();t++){i=e.insertCell();u++}for(t=1;t<=d.getDate();t++){i=e.insertCell();i.textContent=t;if(u++&&u%7==0)e=n.insertRow()}};